import React, { useState } from 'react';
import '../Styles/NavMenu.css';

function NavMenu() {
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  return (
    <div className="nav-menu">
      <button className="menu-button" onClick={toggleDropdown}>
        <span className="line"></span>
        <span className="line"></span>
        <span className="line"></span>
      </button>
      {isDropdownOpen && (
      <div className="dropdown-menu" style={{ display: isDropdownOpen ? 'flex' : 'none' }}>
          <ul>
            <li><button className="close-button" onClick={toggleDropdown}>X</button></li>
            <li className="nav-item"><a href="/">HOME</a></li>
            <li><hr /></li>
            <li className="nav-item"><a href="/visit">VISIT</a></li>
            <li><hr /></li>
            <li className="nav-item"><a href="#" onClick={(e) => e.preventDefault()}>TICKETS</a></li>
            <li><hr /></li>
            <li className="nav-item"><a href="#" onClick={(e) => e.preventDefault()}>EXPLORE</a></li>
            <li className='subHeading'><a href="#" onClick={(e) => e.preventDefault()}>Exhibitions</a></li>
            <li className='subHeading'><a href="#" onClick={(e) => e.preventDefault()}>Collections</a></li>
            <li><hr /></li>
            <li className="nav-item"><a href="#" onClick={(e) => e.preventDefault()}>ABOUT</a></li>
            <li><hr /></li>
            <li className="nav-item"><a href="#" onClick={(e) => e.preventDefault()}>CONTACT</a></li>
            <li><hr /></li>
            <li><a className="red" href="#" onClick={(e) => e.preventDefault()}>DONATE</a></li>
          </ul>
        </div>
      )}
    </div>
  );
}

export default NavMenu;
