import React, { useState, useEffect } from 'react';
import '../Styles/Slideshow.css'; // Import the CSS file for slideshow styles

function Slideshow({ images }) {
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage === images.length - 1 ? 0 : prevImage + 1));
    }, 5000); // Adjust the interval duration as needed (e.g., 5000ms = 5 seconds)

    return () => {
      clearInterval(interval);
    };
  }, [images.length]);

  const handleImageChange = (index) => {
    setCurrentImage(index);
  };

  return (
    <div className="slideshow">
      {images.map((image, index) => (
        <div
          key={index}
          className={`slideshow-slide ${currentImage === index ? 'active' : ''}`}
          onClick={() => handleImageChange(index)}
        >
          <img src={image} alt={`Slideshow Image ${index}`} />
        </div>
      ))}
      <div className="slide-indicators">
        {images.map((_, index) => (
          <div
            key={index}
            className={`slide-indicator ${currentImage === index ? 'active' : ''}`}
            onClick={() => handleImageChange(index)}
          />
        ))}
      </div>
    </div>
  );
}

export default Slideshow;
