import React from 'react';
import NavMenu from './NavMenu';
import CollectionCard from './CollectionCard';
import FilterMenu from './FilterMenu';
import '../Styles/General.css';
import 'bootstrap/dist/css/bootstrap.css';
import '../Styles/Collection.css';

function Collection() {
  // Dummy data for the cards
  const cardsData = [
    { title: 'Title 1', date: 'DD-MM-YYYY', artist: 'Artist 1', image: require('../Images/placeholder.jpg') },
    { title: 'Title 2', date: 'DD-MM-YYYY', artist: 'Artist 2', image: require('../Images/placeholder.jpg') },
    { title: 'Title 3', date: 'DD-MM-YYYY', artist: 'Artist 3', image: require('../Images/placeholder.jpg') },
    // Add more card data as needed
  ];

  // Dummy data for the filter menus
  const filterMenusData = [
    {
      title: 'Photographer',
      options: ['Photographer 1', 'Photographer 2', 'Photographer 3']
    },
    {
      title: 'Year',
      options: ['2021', '2022', '2023']
    },
    {
      title: 'Exhibition',
      options: ['Exhibition 1', 'Exhibition 2', 'Exhibition 3']
    }
  ];

  const [searchQuery, setSearchQuery] = useState('');

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const filteredCardsData = cardsData.filter((card) =>
    card.title.toLowerCase().includes(searchQuery.toLowerCase())
  );


  return (
    <div className='inverse-page-setup'>
      <div className='page-content'>
        <h1 className='title'>COLLECTION</h1>
        <hr />
        <div>
          <input
            type='text'
            value={searchQuery}
            onChange={handleSearchChange}
            placeholder='Search...'
            className='search-bar'
          />
        </div>
        <div className='row'>
          <div className='col-3'>
                <div className='filter-container'>
                  {filterMenusData.map((menu, index) => (
                    <div key={index} className='menu-wrapper'>
                      <FilterMenu title={menu.title} options={menu.options} />
                    </div>
                  ))}
                </div>
              </div>
              <div className='col-9'>
                <div className='center'>
                  <div className='row collection'>
                    {cardsData.map((card, index) => (
                      <CollectionCard
                        key={index}
                        title={card.title}
                        date={card.date}
                        artist={card.artist}
                        image={card.image}
                      />
                    ))}
                  </div>
                </div>
              </div>
          </div>
          </div>
      <NavMenu />
    </div>
  );
}

export default Collection;
