import React, { useState } from 'react';
import '../Styles/General.css';
import '../Styles/NavMenu.css';
import Map from './Map';

const NavMenu = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  return (
    <div className="nav-menu">
      <button className="menu-button" onClick={toggleDropdown}>
        <span className="line"></span>
        <span className="line"></span>
        <span className="line"></span>
      </button>
      {isDropdownOpen && (
        <div className="dropdown-menu">
          <button className="close-button" onClick={toggleDropdown}>X</button>
          <Link className = "nav-item" href="/Visit.jsx">VISIT</Link>
          <Link className = "nav-item" href="#">TICKETS</Link>
          <Link className = "nav-item" href="#">EXPLORE</Link>
          <Link className = "nav-item" href="#">Exhibitions</Link>
          <Link className = "nav-item" href="#">Collections</Link>
          <Link className = "nav-item" href="#">ABOUT</Link>
          <Link className = "nav-item" href="#">CONTACT</Link>
          <Link className = "nav-item red" href="#">DONATE</Link>
        </div>
      )}
    </div>
  );
};

export default NavMenu;
