import React from 'react';
import NavMenu from './NavMenu';
import Slideshow from './Slideshow';
import '../Styles/General.css';
import '../Styles/Exhibitions.css';
import 'bootstrap/dist/css/bootstrap.css';

function Exhibitions() {
  const seasonalImages = [
    require('../Images/seasonal/1.jpg'),
    require('../Images/seasonal/2.jpg'),
    require('../Images/seasonal/3.jpg'),
    require('../Images/seasonal/4.jpg'),
  ];

  const permanentImages = [
    require('../Images/permanent/1.jpg'),
    require('../Images/permanent/2.jpg'),
    require('../Images/permanent/3.jpg'),
    require('../Images/permanent/4.jpg'),
  ];

  return (
    <div>
      <div className="exhibitions page-setup">
        <div className="page-content">
          <h1 className="title">EXHIBITIONS</h1>
          <hr />

          <h3>SEASONAL</h3>
          <br />
          <Slideshow images={seasonalImages} />
        </div>
      </div>

      <div className="inverse">
        <div className="page-content">
          <br />
          <br />
          <h3>PERMANENT</h3>
          <br />
          <Slideshow images={permanentImages} />
        </div>
      </div>
      <NavMenu />
    </div>
  );
}

export default Exhibitions;
