import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../Styles/Home.css';
import '../Styles/General.css';
import NavMenu from './NavMenu';

function Home() {
  const images = [
    { src: require('../Images/Home/catexhibit.jpg'), exhibitName: 'HONG KONG SHOP CATS', description: 'Dutch photographer, Marcel Heijnen, captures the lives of Hong Kong\'s Market Cats' },
    { src: require('../Images/Home/forgottenheritage.jpg'), exhibitName: 'FORGOTTEN HERITAGE', description: 'Photographer Matt Emmett\'s exhbition, Forgotten Heritage, focuses on showing sublime and abandonned places.' },
    { src: require('../Images/Home/xray.jpg'), exhibitName: 'X-RAYS OF ORDINARY ELECTRONICS', description: 'In this exhibition, photographer Andre Duman explores the technology hidden beneath the clean exterior of everyday objects through x-ray images.' },
    // Add more images and their respective exhibit names and descriptions
  ];

  const [currentImage, setCurrentImage] = useState(0);
  const [activeSlide, setActiveSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage === images.length - 1 ? 0 : prevImage + 1));
      setActiveSlide((prevSlide) => (prevSlide === images.length - 1 ? 0 : prevSlide + 1));
    }, 5000); // Adjust the interval duration as needed (e.g., 5000ms = 5 seconds)

    return () => {
      clearInterval(interval);
    };
  }, [images.length]);

  const { src, exhibitName, description } = images[currentImage];

  return (
    <div className="page-setup">
      <div className="page-content">
        <h1 className="title">MUSEUM OF PHOTOGRAPHY</h1>
        <hr />
        <div className="row">
        <div className="col-md-5">
            <div className="button-container">
                <Link to="/learn-more" className='home-button button'>
                  LEARN MORE
                </Link>
              <Link to="/get-tickets" className='home-button button'>
                GET TICKETS
              </Link>
            </div>
          </div>
          <div className="col-md-7">
            <div>
              <h2 className="right-align">{exhibitName}</h2>
              <p className="right-align" style={{ width: '100%', wordWrap: 'break-word', textAlign: 'right', marginLeft: 'auto' }}>
                {description}
              </p>
            </div>
          </div>
          
        </div>
        <div className="img-container-home">
          <img className="home-img" src={src} alt="Sample Image" />
          <div className="home-img-border" />
        </div>
        <div className="slide-indicators">
          {images.map((_, index) => (
            <div
              key={index}
              className={`slide-indicator ${activeSlide === index ? 'active' : ''}`}
              onClick={() => {
                setCurrentImage(index);
                setActiveSlide(index);
              }}
            />
          ))}
        </div>
      </div>
      <NavMenu />
    </div>
  );
}

export default Home;
