import React, { useState, useEffect } from 'react';

function Slideshow({ images }) {
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage === images.length - 1 ? 0 : prevImage + 1));
    }, 5000); // Adjust the interval duration as needed (e.g., 5000ms = 5 seconds)

    return () => {
      clearInterval(interval);
    };
  }, [images.length]);

  const handlePrevImage = () => {
    setCurrentImage((prevImage) => (prevImage === 0 ? images.length - 1 : prevImage - 1));
  };

  const handleNextImage = () => {
    setCurrentImage((prevImage) => (prevImage === images.length - 1 ? 0 : prevImage + 1));
  };

  return (
    <div className="slideshow">
      <div className="img-container">
        <img className="slideshow-img" src={images[currentImage].src} alt="Slideshow Image" />
        <div className="img-border" />
      </div>
      <div className="slide-indicators">
        {images.map((_, index) => (
          <div
            key={index}
            className={`slide-indicator ${currentImage === index ? 'active' : ''}`}
            onClick={() => setCurrentImage(index)}
          />
        ))}
      </div>
      <div className="slideshow-controls">
        <button className="slideshow-control prev" onClick={handlePrevImage}>
          PREVIOUS
        </button>
        <button className="slideshow-control next" onClick={handleNextImage}>
          NEXT
        </button>
      </div>
    </div>
  );
}

export default Slideshow;
