import React from 'react';
import NavMenu from './NavMenu';
import CollectionCard from './CollectionCard';
import '../Styles/General.css';
import 'bootstrap/dist/css/bootstrap.css';
import '../Styles/Collection.css';

function Collection() {
  // Dummy data for the cards
  const cardsData = [
    // Add your card data here
  ];

  return (
    <div className='inverse-page-setup'>
      <div className='page-content'>
        <h1 className='title'>COLLECTION</h1>
        <hr />
        <div className='row'>
          <div className='col-3'>
            {/* Filter sidebar */}
            <div className='filter-sidebar'>
              <h3>Filters</h3>
              <div className='filter-menu'>
                <h5>Photographers</h5>
                {/* Checkboxes for photographers */}
                <label>
                  <input type='checkbox' name='photographer' value='Photographer 1' /> Photographer 1
                </label>
                <label>
                  <input type='checkbox' name='photographer' value='Photographer 2' /> Photographer 2
                </label>
                {/* Add more checkboxes as needed */}
              </div>
              <div className='filter-menu'>
                <h5>Year</h5>
                {/* Checkboxes for year */}
                <label>
                  <input type='checkbox' name='year' value='2021' /> 2021
                </label>
                <label>
                  <input type='checkbox' name='year' value='2022' /> 2022
                </label>
                {/* Add more checkboxes as needed */}
              </div>
              <div className='filter-menu'>
                <h5>Exhibition</h5>
                {/* Checkboxes for exhibition */}
                <label>
                  <input type='checkbox' name='exhibition' value='Exhibition 1' /> Exhibition 1
                </label>
                <label>
                  <input type='checkbox' name='exhibition' value='Exhibition 2' /> Exhibition 2
                </label>
                {/* Add more checkboxes as needed */}
              </div>
              {/* Add more filter menus as needed */}
            </div>
          </div>
          <div className='col-9'>
            {/* Collection cards */}
            <div className='center'>
              <div className='row collection'>
                {cardsData.map((card, index) => (
                  <CollectionCard
                    key={index}
                    title={card.title}
                    date={card.date}
                    artist={card.artist}
                    image={card.image}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
      <NavMenu />
    </div>
  );
}

export default Collection;
